mapper.insert:
INSERT INTO
 	tbl_mycardinfo(
 		mycardnum
 		, userid
		, company
		, name1
		, name2
		, name3
		, phone
		, tel
		, fax
		, email
		, address
		, job
		, department
		, otherinfo
		, otherinfo2
		, frontimgsaved
		, backimgsaved
		, frontimgoriginal
		, backimgoriginal
 	)values(
 		CONCAT('mbc',TO_CHAR(LPAD(seq_mycardnum.nextval,27,0)))
 		,#{userid}
 		,#{company, jdbcType=VARCHAR}
 		,#{name1, jdbcType=VARCHAR}
 		,#{name2, jdbcType=VARCHAR}
 		,#{name3, jdbcType=VARCHAR}
 		,#{phone, jdbcType=VARCHAR}
 		,#{tel, jdbcType=VARCHAR}
 		,#{fax, jdbcType=VARCHAR}
 		,#{email, jdbcType=VARCHAR}
 		,#{address, jdbcType=VARCHAR}
 		,#{job, jdbcType=VARCHAR}
 		,#{department, jdbcType=VARCHAR}
 		,#{otherinfo, jdbcType=LONGVARCHAR}
 		,#{otherinfo2, jdbcType=VARCHAR}
 		,#{frontimgsaved}
 		,#{backimgsaved, jdbcType=VARCHAR}
 		,#{frontimgoriginal, jdbcType=VARCHAR}
 		,#{backimgoriginal, jdbcType=VARCHAR}
 	)

mapper.update:
	UPDATE 
	 	tbl_mycardinfo
	SET 	
 		company = #{company, jdbcType=VARCHAR}
 		, name1 = #{name1, jdbcType=VARCHAR}
 		, name2 = #{name2, jdbcType=VARCHAR}
 		, name3 = #{name3, jdbcType=VARCHAR}
 		, phone = #{phone, jdbcType=VARCHAR}
 		, tel 	= #{tel, jdbcType=VARCHAR}
 		, fax 	= #{fax, jdbcType=VARCHAR}
 		, email = #{email, jdbcType=VARCHAR}
 		, address = #{address, jdbcType=VARCHAR}
 		, job 	= #{job, jdbcType=VARCHAR}
 		, department = #{department, jdbcType=VARCHAR}
 		, otherinfo2 = #{otherinfo2, jdbcType=VARCHAR}
	 WHERE
	 	userid like #{userid}
	 	AND
	 	mycardnum like #{mycardnum}

mapper.select:
SELECT mycardnum , company , name1 , name2 , name3 , phone , tel , fax , email , address , job , department , otherinfo2 , frontimgsaved , backimgsaved , cardorder , frontimgoriginal , backimgoriginal FROM tbl_mycardinfo WHERE userid like #{userid}
AND
mycardnum = #{mycardnum}

mapper.delete:

DELETE FROM
	tbl_mycardinfo

WHERE
	userid like #{userid}

	AND

	mycardnum like #{mycardnum}
