mapper.insert:

INSERT INTO
	tbl_userinfo (	
		userid
		, userpw
		, username
	)
VALUES (
	#{userid}
	, #{userpw}
	, #{username}
)

mapper.select:
SELECT userid , username , emailverify FROM tbl_userinfo WHERE userid like #{userid}
AND
userpw like #{userpw}

mapper.update:

UPDATE
	tbl_userinfo
SET
	username = #{username}


		, userpw = #{userpw}

WHERE
	userid like #{userid}
